    <style class="css">
        .analytics{
            border:0.1px solid var(--secondary);
            position:relative;
            overflow: hidden;
        }
        .analytics .group{
            z-index:500;
        }
     .analytics::before{
            content:'';
            position:absolute;
            left:10%;
            top:10%;
            width:40%;
            height:40%;
            background:var(--primary);
            border-radius:50%;
            filter:blur(30px);
            z-index:10;
        }
         .analytics::after{
            content:'';
            position:absolute;
            right:10%;
            bottom:10%;
            width:20%;
            height:20%;
            background:var(--primary);
            border-radius:50%;
            filter:blur(20px);
            z-index:10;
        }
    </style>
@endsection
